function checkInput(item,mesages){if(""===item.value)return alert(mesages),item.classList.add("border"),item.focus(),!1;item.classList.remove("border")}function validateForm(registerInfo){switch(!1){case checkInput(registerInfo.name,"You have not entered your name"):case checkInput(registerInfo.phone,"You did not enter a phone number"):case checkInput(registerInfo.company,"You have not entered the company name"):case checkInput(registerInfo.email,"You did not enter E-mail address"):case checkInput(registerInfo.note,"You have not entered a question"):break;default:sendAPI(registerInfo),alert("You have successfully submitted"),registerInfo.name.value="",registerInfo.phone.value="",registerInfo.company.value="",registerInfo.website.value="",registerInfo.country.value="",registerInfo.email.value="",registerInfo.note.value=""}}!function(){const registerForm=document.getElementById("register-form");registerForm.addEventListener("submit",e=>{e.preventDefault();const registerInfo={name:registerForm.iname,phone:registerForm.iphone,company:registerForm.icompany,website:registerForm.iwebsite,country:registerForm.icountry,email:registerForm.iemail,note:registerForm.inote};validateForm(registerInfo)})}();